import React, { useState, useEffect } from 'react';
import { gql, useMutation } from '@apollo/client';
import './Signup.css';

const Signup = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  useEffect(() => {
    document.title = "Sign Up";
  }, []);
  const handleSubmit = (e) => {
    e.preventDefault();
    const First_Name = e.target.First_Name.value;
    const Last_Name = e.target.Last_Name.value;
    const Date_of_Birth = e.target.Date_of_Birth.value;
    const Email = e.target.Email.value;
    const Password = e.target.Password.value;

    createUser({ variables: { First_Name, Last_Name, Date_of_Birth, Email, Password } })
      .then(() => {
        // Refetch the users after successful creation
        // You can skip this if you're using real-time updates or subscriptions
        client.resetStore();
      })
      .catch((error) => {
        console.error(error);
      });

  };


  const CREATE_USER = gql`
  mutation CreateUser(
    $first_name: String!
    $last_name: String!
    $email: String!
    $password: String!
    $birthday: String!
    $gender: String!
  ) {
    createUser(
      first_name: $first_name
      last_name: $last_name
      email: $email
      password: $password
      birthday: $birthday
      gender: $gender
    ) {
      first_name
      last_name
      email
      password
      birthday
      gender
    }
  }
`;
  const [createUser] = useMutation(CREATE_USER);

  return (
    <div className="form-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>First Name:</label>
          <input
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Last Name:</label>
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Date of Birth:</label>
          <input
            type="date"
            value={dateOfBirth}
            onChange={(e) => setDateOfBirth(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default Signup;
